graphql.query:
mutation CreateProject($deleteProjectId: Int) {
  deleteProject(id: $deleteProjectId)
}

query Me {
  me {
    name
    userAdvance {
      personalGoal
      language

    }
    userFeedback {
      grate
    }
    project {
      name
    }
    projectMembers {
      project {
        name
      }
      roles
    }
    selfProject
    joinedProject
  }
}

query AllUsers {
  allUsers {
    id
    email
    name
  }
}

query DetailUser($detailUserId: Int) {
  detailUser(id: $detailUserId) {
    id
    first_name
    name
    email
    phone_number
    address
    date_of_birth
    gender
    userAdvance{
        id
        userId
        roles
        language
        skill
        info
        plan
        goal
        numberFramework
        selfProject{
            id
            name
            attachments
        }
        joinProject{
            id 
            position
            project{
                id
                name
                category
                attachments
                timeToDo
            }
        }
    }
  }
}

