  detailUser(id: $detailUserId) {
    id
    first_name
    name
    email
    phone_number
    address
    date_of_birth
    gender
    avatar_attachment
    background_attachment
    selfProject{
            id
            name
            attachments
        }
    joinProject{
            id 
            position
            project{
                id
                name
                category
                attachments
                timeToDo
            }
        }
    userAdvance{
        id
        userId
        roles
        language
        skill
        info
        plan
        goal

    }
  }
}

query SearchUsers($name: String, $roles: JSON, 
$page: String, $limit: String) {
  searchUsers(name: $name, roles: $roles, page: $page, limit: $limit) {
    id
    name
    email
    phone_number
    userAdvance {
      id
      roles
      goal
      plan
      userId
    }
  }
}

